import axios from "axios"
import { useEffect, useState } from "react"

export const Chat=()=>{
     
    const [message,setMessage]=useState<string>() 

    const  messageHandler= async()=>{
        axios.post('http://localhost:3001/new-message',message)  
           .then(res=>console.log(res, message)) 
           .then(()=>setMessage(""))

    }

    
    return  <div className="h-96 w-full flex justify-center flex-col items-center">
                <input className="outline-none border-black border-solid border-2 w-60 mt-2 rounded-1xl"
                       onChange={e=> setMessage(e.target.value)}></input>
                <button className="border-black border-2 mx-2 mt-2 h-7 text-sm  w-40">Send</button>
            </div>
}
