import { ServerResponse } from "http";
import React, { useEffect, useState, useRef } from "react";
import { useDebouncer } from "../hooks/useDeboundcer";
import { useSearchUsersQuery } from "../store/github/github.api";

export default function HomePage(){ 
     
     const [search, setSearch]=useState<string>("")
     const debounced=useDebouncer(search)
     const {isLoading, isError, data}=useSearchUsersQuery(debounced, {
                                                          skip: debounced.length<3
     })
    

    useEffect(()=>{
        console.log(search)
         },[debounced])
     
     
    
    return <div className="mt-10 mx-5 flex-col flex space-between items-center">
               <div>
                        <input className="mx-2 outline-none border-black border-solid border-2 w-60 my-2 rounded-1xl"
                            onChange={(e)=>setSearch(e.target.value)}> 
                        </input>
                    
                        <button  className="border-black border-2 height rounded-5 w-40">Click</button> 
                </div>
               {isError&&<p className="my-2 text-red-600 text-center">Something went wrong with the fetching</p>}
                
           </div>
}
