import { ServerResponse } from "http";
import React, { useEffect, useState, useRef } from "react";
import { useSearchUsersQuery } from "../store/github/github.api";

export default function HomePage(){ 
     
     
     const [data, setData]=useState<ServerResponse>([])
     const refWord=useRef<any>()
    

     const searchHandler=()=>{  
        const {isLoading,isError, data}=useSearchUsersQuery(refWord.current.value)
        setData([{isLoading:isLoading,isError:isError, data:{data}}])
        refWord.current.value=""
        
     }
    
    return <div style={{ 
                        marginTop:"16vh"
    }}> 
                <input ref={refWord}
                       style={{
                               background:"grey",
                               color: "white"
                       }}>
                </input>
                <button onClick={()=>console.log(refWord.current.value)}> click</button>
           </div>
}
